DROP TABLE IF EXISTS dt_list_content;
CREATE TABLE `dt_list_content` (
  `guid` varchar(16) NOT NULL default '',
  `pageId` int(11) NOT NULL default '0',
  `pageName` varchar(60) NOT NULL default '',
  `content` longtext,
  KEY `guid` (`guid`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;


